#!/bin/sh
cubevar_app_name="myplaceonline"
cubevar_app_web_dir="/var/www/html/myplaceonline"
cubevar_app_db_host="db1-internal.myplaceonline.com"
cubevar_app_db_dbname="myplaceonline_production"
cubevar_app_db_dbuser="myplaceonline"
cubevar_app_server_name="myplaceonline.com"
cubevar_app_server_protocol="https"
cubevar_app_smtp_user="myplaceonline"
cubevar_app_mail_from="contact@myplaceonline.com"
cubevar_app_root_email="root@myplaceonline.com"
cubevar_app_tmpdir="/var/tmp/"
cubevar_app_full_text_search_target="db2-internal.myplaceonline.com:9200"
cubevar_app_networkping="104.236.182.29"
cubevar_app_rails_gc_max_newspace="33554432"
cubevar_app_rails_gc_max_oldspace="67108864"
cubevar_app_rails_threads_per_node="2"
cubevar_app_rails_environment="production"
cubevar_app_nginx_dir="/etc/nginx/"
cubevar_app_nginx_user="nginx"
cubevar_app_nginx_gzip_static="on"
cubevar_app_nginx_passenger_install_method="source"
